    // 退出登录
    async logout() {
      try {
        await this.$confirm('此操作将退出登录, 是否继续?', '提示', {
          confirmButtonText: '确定',
          cancelButtonText: '取消',
          type: 'warning'
        })
        this.$message.success('退出成功！')
        this.$store.commit('user/logout')
        this.$router.push('/login')
      } catch (error) {
        console.log(error)
      }
    }
  }
}
